import React from "react";
import './AffichageMot.css';

const ChampTest = (props) => {
 const {essai} = props;
 const changerLettre = (texte) =>{
    const lettre = texte.value;
    console.log(lettre);
 };
  return (
    <div>

      <input onChange={()=>changerLettre.bind(this)} className="lettre" type="text" maxLength='1' ></input>
      <button onClick={essai.bind(document.querySelector('.lettre').value)}>Essayer</button>
    </div>
  );
};

export default ChampTest;
