import React, { useState } from 'react';
import AdTeamMember from './AdTeamMember';
import Equipe from './Equipe';


const FormerEquipe = (props) => {
    const [arrEquipe, setArrEquipe] = useState(new Array(new Array()));
    const [nbTeam,setNbTeam]=useState(0);
  const ajouter= () =>{
      let arrEquipeAMonter = arrEquipe;
      arrEquipeAMonter[nbTeam].push(document.querySelector(".nomParticipant").value);
      setArrEquipe(arrEquipeAMonter);
      console.log(arrEquipe);
  };
  const ajouterEquipe= () =>{
        let arrEquipeAMonter = arrEquipe;
        setNbTeam(nbTeam + 1);
        arrEquipeAMonter.push(new Array());
        setArrEquipe(arrEquipeAMonter);
        console.log(arrEquipe);
  };
  return (
    <div>
        <h2>créer vos équipes</h2>
        <h3>ajouter les membre de l'équipe {nbTeam + 1}</h3>
        {arrEquipe.map((item, index) => (
            arrEquipe[index].map((item, indexEquipier) => (
                <p key={indexEquipier}>{arrEquipe[index][indexEquipier]}</p>
            ))
        ))}
        <AdTeamMember ajouter={()=>ajouter()}/>
        <button onClick={()=>ajouterEquipe()}>ajouter une équipe</button>
    </div>
  );
}

export default FormerEquipe;
