import React, { useState } from 'react';
import AdTeamMember from './AdTeamMember';
import Equipe from './Equipe';


const FormerEquipe = (props) => {
    const [arrEquipe, setArrEquipe] = useState(new Array(new Array()));
    const [nbTeam,setNbTeam]=useState(0);
    const [equipe, setEquipe]= useState();
    const [nbEquipier, setNbEquipier] = useState(0);
    const [isSetting, setIsSetting] = useState(true);
  const ajouter= () =>{
      let arrEquipeAMonter = arrEquipe;
      arrEquipeAMonter[nbTeam].push(document.querySelector(".nomParticipant").value);
      setNbEquipier(nbEquipier+1);
      if(nbEquipier<2)
      {
        document.querySelector(".ajoutEquipe").disabled=true;
        document.querySelector(".debuter").disabled=true;
      }
      else
      {
        document.querySelector(".ajoutEquipe").disabled=false;
        if(nbTeam>0)
        {
            document.querySelector(".debuter").disabled=false;
        }
      }
      if(nbEquipier == 5)
      {
          document.querySelector(".boutonAjouter").disabled=true;
      }
      setArrEquipe(arrEquipeAMonter);
      setEquipe(<Equipe arrEquipe={arrEquipe}/>);
  };
  const ajouterEquipe= () =>{
        let arrEquipeAMonter = arrEquipe;
        document.querySelector(".debuter").disabled=true;
        if(arrEquipeAMonter[nbTeam].length<=1)
        {
            document.querySelector(".ajoutEquipe").disabled=true; 
        } 
        else{
            setNbEquipier(0);
            if(nbTeam == 3){
                 document.querySelector(".ajoutEquipe").disabled=true;
            }
            setNbTeam(nbTeam + 1);
            document.querySelector(".boutonAjouter").disabled=false;
            arrEquipeAMonter.push(new Array());
            setArrEquipe(arrEquipeAMonter);
            setEquipe(<Equipe arrEquipe={arrEquipe}/>);
        }
  };
  const handleDebuter = () =>{
        this.props.handleCallback(arrEquipe);
  };
  return (
    <div>
        <h2>créer vos équipes</h2>
        <h3>ajouter les membre de l'équipe {nbTeam + 1}</h3>
        <p>vous devez ajouter jusqu'à 4 joueus par équipe</p>
        {equipe}
        <AdTeamMember ajouter={()=>ajouter()}/>
        <button className="ajoutEquipe" onClick={()=>ajouterEquipe()}>ajouter une équipe</button>
        <button onClick={()=>handleDebuter()} className='debuter' disabled={true}>Commencer la partie</button>

    </div>
  );
}

export default FormerEquipe;
