import React, { useState } from "react";
import './AffichageMot.css';

const ChampTest = (props) => {
 const {essai} = props;
 const [lettre, setLettre] = useState('');
//  const changerLettre = (texte) =>{
//     const lettre = document.querySelector('.lettre').value;
//     console.log(lettre);
//  };
  return (
    <div>

      <input onChange={setLettre(this.value)} className="lettre" type="text" maxLength='1' ></input>

      <button onClick={essai.bind(lettre)}>Essayer</button>
    </div>
  );
};

export default ChampTest;
